import{_ as e,c as n,o,ah as a}from"./chunks/framework.C9w-46Qr.js";const g=JSON.parse('{"title":"Terms you need to understand","description":"","frontmatter":{},"headers":[],"relativePath":"docs/Terms-you-need-to-understand.md","filePath":"docs/Terms-you-need-to-understand.md"}'),r={name:"docs/Terms-you-need-to-understand.md"};function s(d,t,i,l,m,c){return o(),n("div",null,[...t[0]||(t[0]=[a('<h1 id="terms-you-need-to-understand" tabindex="-1">Terms you need to understand <a class="header-anchor" href="#terms-you-need-to-understand" aria-label="Permalink to “Terms you need to understand”">​</a></h1><p>Here we introduce the most important terms we use and explain what they mean in the context of OAG.</p><table tabindex="0"><thead><tr><th>Term</th><th>Explanation</th></tr></thead><tbody><tr><td>Downstream System</td><td>Any system or application that is called (forwarded to) by OAG. Often services and applications implementing business logic.</td></tr><tr><td>Upstream System</td><td>Callers (initiating requests) of OAG. Often also known as clients.</td></tr><tr><td>Logging</td><td>Writing information about the inner workings (state) of OAG to a file or other destination.</td></tr><tr><td>Tracing</td><td>The goal of tracing is to following a flow of activity and data processing (often known as request) through one or more systems/applications involved.</td></tr><tr><td>Log correlation / Correlation logging</td><td>The ability to assign log statements to a flow effectively enabling tracing. Usually log correlation is implemented by means of assigning a unique id to each flow and writing this unique id out with every log statement.</td></tr></tbody></table>',3)])])}const h=e(r,[["render",s]]);export{g as __pageData,h as default};
